import React from 'react';
import '../style/home.css'; // Importás el CSS
import '../activityDetails/components/ActivityCard';
import react, {useState} from 'react' ;
import ActivityCard from '../activityDetails/components/ActivityCard';



const HomePage = () => {

    const [search, setSearch] = useState("");
    const [selectedActivity, setSelectedActivity] = useState(null);




    const user = {
        name: "Martina Polanco",
        image: "",
        esAdmin: true
    };
    const defaultProfileImage ="https://i.pinimg.com/474x/bd/f4/d3/bdf4d3fe1f9a17136319df951fe9b3e0.jpg"
    
    
    
    const [activities] = useState([
        {
          id: 1,
          title: "Spinning",
          day: "Lunes",
          time: "18:00",
          professor: "Laura",
          category: "Cardio",
          description: "Clase intensa de cardio sobre bicicleta fija.",
          imagen: "data:image/jpeg;base64,"
        },
        {
          id: 2,
          title: "Funcional",
          day: "Martes",
          time: "19:00",
          professor: "Carlos",
          category: "Fuerza",
          description: "Entrenamiento funcional de cuerpo completo.",
          imagen: "data:image/jpeg;base64,"
        }
      ]);
    

    
    const filteredActivities = activities.filter((activity) =>
        activity.title.toLowerCase().includes(search.toLowerCase()) ||
        activity.category.toLowerCase().includes(search.toLowerCase()) ||
        activity.day.toLowerCase().includes(search.toLowerCase())
    );










  return (
    <div className="home-container">   {/* DIV PRINCIPAL*/}
        <div className="gridContainer"> {/* DISEÑO DEL CUADRO CON INFORMACIOIN DE LA PAGINA*/}
        <div className="parent">
            <div className="div1">  {/* DIV CON DATOS MENORES DE LAS ACTIVIDADES*/}
                <div className="left">
                {selectedActivity ? (
                    <img src={selectedActivity.imagen || defaultProfileImage} alt="Actividad" className="activityImg" />
                ) : (
                    <p></p>
                )}
                </div>
                <div className="rigth">
                    {selectedActivity ? (
                        <>
                        <p><strong>Profesor: </strong>{selectedActivity.professor}</p>
                        <p><strong>Actividad: {selectedActivity.category}</strong></p>
                        <p><strong>Día: {selectedActivity.day}</strong></p>
                        <p><strong>Hora: {selectedActivity.time}</strong></p>
                        <button className="activityButton">Ver mas info.</button>
                        </>
                      ) : (
                        <p>Hacé clic en una actividad para ver los detalles</p>
                    )}
                </div>
                    
                </div>
                <div className="div2">  {/* DIV CON DATOS MENORES DEL USUARIO*/}
                    <div className="userData">
                        <div className="userDataLeft">
                            <img src={user.image ? user.image : defaultProfileImage} alt=" " className="profilePhoto" />
                        </div>
                        <div className="userDataRigth">
                            <p><strong>{user.name}</strong></p>
                            {user.esAdmin && (<span className="admin-badge">A</span>)}  {/* IDENTIFICADOR DE SI ES ADMIN*/}
                        </div>
                    </div>
                </div>
                <div className="div3">  {/* DIV CON BUSCADOR Y TODAS LAS ACTIVIDADES DEL GIMNASIO*/}
                    <input 
                    type="text" 
                    placeholder = "Buscar por titulo, categoria o dia"
                    value={search}
                    onChange={(e) => setSearch(e.target.value)}
                    className="search"
                    />
                    {filteredActivities.length === 0 ? (
                        <p>No se encontraron actividades.</p>
                    ) : (
                        filteredActivities.map(activity => (
                            <ActivityCard
                            key={activity.id}
                            activity={activity}
                            onClick={() => setSelectedActivity(activity)}
                            />

                        ))
                    )}

                </div>
            </div>
        </div>  
        {/* BOTONES SOLO DISPONIBLES PARA ADMINISTRADORES*/}
        {user.esAdmin && (
            <div className="buttonContainer">
                <button><strong>Lista de Usuarios</strong></button>
                <button><strong>Agregar actividad</strong></button>
                <button><strong>Eliminar actividad</strong></button>
            </div>
            )}
    </div>
    
  );
};

export default HomePage;
